mapper.insert:
INSERT
INTO mem_info
VALUES (
null
, #{mem_id}
, #{mem_name}
, #{mem_nickname}
, #{mem_passwd}
,
CONCAT(#{sample6_postcode},'/',#{sample6_address},'/',#{sample6_detailAddress},'/',#{sample6_extraAddress})
, #{mem_birthday}
, #{mem_interest}
, #{mem_email}
, CONCAT(#{phone1},
'-', #{phone2}, '-', #{phone3}) -- 휴대폰 번호
, #{role} -- 멤버 권한
, #{mem_bio} -- 프로필 자기소개
, #{mem_status} -- 회원 상태
, 0 -- 회원 등급
, #{mem_profileImageUrl} -- 회원 프로필 사진
, now() -- 회원 가입일
)

mapper.update:
UPDATE mem_info SET
    mem_passwd = #{mem_passwd},
    mem_nickname = #{mem_nickname},
    mem_email = #{mem_email}
WHERE
    mem_id = #{mem_id}

UPDATE mem_info
SET mem_name=#{mem_name}
, mem_nickname=#{mem_nickname}

  , mem_birthday = CONCAT(#{mem_bir1}, '-', #{mem_bir2}, '-', #{mem_bir3})


  , mem_mtel = CONCAT(#{phone1}, '-', #{phone2}, '-', #{phone3})


  , mem_address = CONCAT(#{sample6_postcode},'/',#{sample6_address},'/',#{sample6_detailAddress},'/',#{sample6_extraAddress})

WHERE mem_id = #{mem_id}

UPDATE mem_info
SET 

    mem_passwd = #{newPasswd1}

WHERE mem_id = #{member.mem_id}

mapper.select:
SELECT mem_passwd
FROM mem_info
WHERE mem_id = #{mem_id}

SELECT mem_id
FROM mem_info
WHERE
mem_name = #{mem_name}
and mem_mtel = #{mem_mtel}

SELECT mem_email
FROM
mem_info
WHERE
mem_id = #{mem_id}
and mem_name = #{mem_name}
and mem_email = #{mem_email}

mapper.update:
update mem_info
set mem_passwd = #{mem_passwd}
where
mem_id = #{mem_id}
and mem_name = #{mem_name}

UPDATE mem_info
SET 

        mem_profileImageUrl = #{mem_profileImageUrl},

    mem_nickname = #{mem_nickname},
    mem_bio = #{mem_bio}
WHERE mem_idx = #{mem_idx}

UPDATE mem_info
SET role = "ROLE_REST"
WHERE mem_idx = #{sId}

mapper.select:
SELECT * FROM mem_info
WHERE
	mem_nickname LIKE #{keyword}
	AND
	mem_sign_date < #{searchDate}
LIMIT #{startCount}, #{setPageListLimit}
